-- simple delay function-- easier to use than TimerMatch() (but not that versatile)function dt()	local i		for i=20,0,-1 do		print(i)		pic.TimerDelay(i*50)	endend-- detect and mount SD-card-- exits on ESC from consolefunction dm()	print("wating for card insertion...")	while pic.CardDetect() == 0 do		io.write(".")				if pic.ConsoleReadChar() == 27 then			print("\r\ngiving up...")			return 0		end	end		io.flush()	io.write("\ncard found, about to mount: ")	io.flush()		pic.TimerDelay(500)	if pic.CardMount() == 0 then		print("ERROR")		return	end		print("OK")	return 1end-- mount card, if available-- list all files, directories and sizes of the root dir entriesfunction lf()	local nam, size	if pic.CardMount() == 0 then		print("ERROR MOUNTING CARD")		return 0	end		print()	nam,size=pic.CardFindFirst("*.*",2)	if nam ~= nil then		while 1 do			print(nam,size)			nam,size=pic.CardFindNext()			if nam==nil then				break			end		end	else		print("empty")	endend-- open a file by name and display its contents-- if no file name is specified, the first file found is used-- make sure this is a text file ;-)-- Note: It might look strange depending on your terminal-- settings (newline, linefeed, ...)function of(fname)	local fin	if pic.CardMount() == 0 then		print("ERROR MOUNTING CARD")		return 0	end		if fname==nil then		fname=pic.CardFindFirst("*.*")		if fname==nil then			print("NO FILES FOUND")			return 0		end	end		print("OPENING "..fname)	fin=io.open(fname,"r")	if fin==nil then		print("ERROR OPENING FILE")		return 0	end	print("---")	for lines in fin:lines() do		print(lines)	end	print("---")		fin:close()	print("FILE CLOSED")	end-- enter a file name, create that file on SD-card and-- enter some text, empty line quits!-- Notes (READ EM!):--  - Do not use delete, backspace or other "special" characters.--  - use a "8+3" filename--  - This is just a demonstration!function cf()	local tmp	local fout	if pic.CardMount() == 0 then		print("ERROR MOUNTING CARD")		return 0	end		io.write("ENTER FILENAME: ")	tmp=io.read()		if string.len(tmp)==0 then		print("QUIT...")		return 0	end	fout=io.open(tmp,"w")		if fout==nil then		print("UNABLE TO OPEN FILE")		return 0	end		print("ENTER SOME TEXT, QUIT WITH AN EMPTY LINE")		while 1 do		tmp=io.read()		print()		if string.len(tmp)==0 then			break		end		fout:write("\r\n")		fout:write(tmp)	end		fout:flush()	fout:close()	print("\r\nNOW STICK THAT CARD INTO YOUR DIRTY")	print("PC CARD-READER AND LOOK WHAT YOU HAVE DONE...")	return 1end